mapper.select:
select tc.*,pv.bed_no,dept.dept_name as patDeptName,
case when tc.unplan_oper_flag = '1' then '是' else '否' end as edit_person_cd
from td_ca_operation_apply tc
left join pat_visitinfo pv on tc.patient_id=pv.patient_id
left join tm_bs_dept dept on dept.dept_code=pv.dept_cd

     and tc.patient_id = #{patientId}
     and tc.admission_no = #{admissionNo}

        and (tc.patient_name like concat(concat('%', #{patientName}), '%') or
        tc.admission_no like concat(concat('%', #{patientName}), '%')
        or
        tc.case_no like concat(concat('%', #{patientName}), '%')
        )

     and tc.apply_doc_cd = #{applyDocCd}
     and (tc.apply_doc_name like concat(concat('%', #{applyDocName}), '%') or tc.apply_doc_cd like concat(concat('%', #{applyDocName}), '%'))
     and tc.apply_date = #{applyDate}
     and tc.exec_dept_cd = #{execDeptCd}
     and tc.exec_dept_name like concat(concat('%', #{execDeptName}), '%')
     and tc.plan_oper_time = #{planOperTime}
     and tc.oper_type_cd = #{operTypeCd}
     and (tc.apply_dept_cd = #{applyDeptCd} or tc.exec_dept_cd = #{applyDeptCd})
     and tc.audit_doc_cd = #{auditDocCd}
     and tc.status = #{status}
     and tc.oper_doc_cd = #{operDocCd}
     and (tc.oper_doc_name like concat(concat('%', #{operDocName}), '%') or tc.oper_doc_cd like concat(concat('%', #{operDocName}), '%'))

        and tc.status in

            #{status}


     and tc.plan_oper_time between #{planOperStartTime} and #{planOperEndTime}

        and tc.apply_date >= #{operStartDate}


        and tc.apply_date < #{operEndDate}


         and tc.oper_date between #{operStartDate} and #{operEndDate}
         and tc.apply_date between #{operStartDate} and #{operEndDate}


order by tc.apply_date

