update td_ca_operation_apply
set status = #{status}
where apply_form_no in

    #{applyFormNo}

mapper.select:
select app.oper_date as operDateTime, app.oper_room, app.oper_room_cd, app.oper_table, app.preoper_diag1_name as operDiag, app.plan_oper1_name as operName, app.oper_doc_name as operatorName, app.oper_aid1_name as operAid1, app.anest_meth_name as anesthesia, app.anest_name as anesthetist, app.status as oper_status, app.apply_dept_name dept_name, app.patient_name, app.admission_no as inp_no, app.sex, app.age, app.case_no record_no, app.oper_type_cd, app.PLAN_OPER_TIME, app.APPLY_DATE, b.bedid as bed_no from td_ca_operation_apply app left join t_ar_medicalinformation_all b on app.admission_no = b.admission_no
and app.status not in
#{operStatus}
and app.SHIFT_DATE >= #{shiftDateBegin}
and app.SHIFT_DATE < #{shiftDateEnd}
and app.plan_oper_time >= #{planOperTimeBegin}
and app.plan_oper_time < #{planOperTimeEnd}
and app.APPLY_DEPT_CD=#{deptCd}
and (app.admission_no like concat(concat('%', #{inpNo}), '%') or app.patient_name like concat(concat('%', #{inpNo}), '%') or app.case_no like concat(concat('%', #{inpNo}), '%'))
and app.exec_dept_cd=#{execDeptCd}

SELECT
*
FROM
TD_CA_OPERATION_APPLY
where STATUS='06' and OPER_DATE is not null and EXEC_DEPT_CD=#{execDeptCd}

    and oper_date between #{operStartDate} and #{operEndDate}

select a.surgical_room_code as surgicalRoomCode, count(1) as count from td_ca_operation_room a left join td_ca_operation_apply b on b.oper_room = a.surgical_room_code
a.status = '1' and b.status in('06','08')
and b.apply_dept_cd = #{deptCd}
and to_char(b.oper_date,'yyyy-MM-dd') = #{currentDate}
group by a.surgical_room_code
